<script>
import { ipcRenderer } from 'electron'
import constants from '../../common/constants'
const copy = require('copy-to-clipboard')
const packagejson = require('../../../package.json')
const isOnline = require('is-online')
export default {
  name: 'setting',
  data () {
    return {
      supportCode: 0, // 0不显示  1升级中  2升级完成  3升级失败  4绑定码
      status: 3, // 1正常状态  2访问错误 3加载中 4发现新版本 5加载新标准 6稍后左下角显示新版本
      progress: 0, // 更新进度
      name: this.$t('m.setting.info'),
      SN: '',
      bindCode: '',
      isLatest: false,
      updateType: '',
      description: '',
      isCosUpdate: false,
      centerDialogVisible: false,
      cosOldVersionData: '',
      cosNewVersionData: '',
      showError: false,
      errorInfo: {},
      softUpdateInfo: '',
      softOldVersionData: packagejson.version,
      softNewVersionData: '2.1.0',
      copyTooltipDisabled: false
    }
  },
  destroyed () {
    // 移除事件监听
    ipcRenderer.removeAllListeners('updateMessage')
    ipcRenderer.removeAllListeners('downloadProgress')
    // ipcRenderer.removeAllListeners('isUpdateNow')
  },
  mounted () {
    // 检测网络
    isOnline({
      timeout: 5000
      // version: "v4" // v4 or v6
    }).then(online => {
      if (online) {
        this.getSn()
        // TODO 软件升级检测
        if (process.env.NODE_ENV === 'production') {
          this.checkSoftUpdate()
        }
      } else {
        this.status = 2
      }
    })
  },
  methods: {
    copyText () {
      const res = copy(this.SN)
      if (res) {
        this.copyTooltipDisabled = true
        setTimeout(() => {
          this.copyTooltipDisabled = false
        }, 1000)
      }
    },
    changeCode (code) {
      this.supportCode = code
    },
    later () {
      // 稍后更新
      this.status = 6
    },
    // updateNow(){
    //     //立刻更新
    //     this.status=5
    // },
    compareByFirmwareVersion () {
      // 如果cos版本不一致，提示用户更新
      if (this.cosNewVersionData === null || this.cosNewVersionData === '') {
        this.isCosUpdate = false
      } else {
        if (this.cosOldVersionData === this.cosNewVersionData) {
          this.isCosUpdate = false
        } else {
          // 升级按钮变黑
          this.isCosUpdate = true
          this.$store.state.isCosUpdate = true
          this.$store.state.cosNewVersionData = this.cosNewVersionData

          // 检查完成
          this.status = 1
          // 提示更新信息
          // this.openErrorView(this.description)
        }
      }
    },
    exportBindCode () {
      const result = ipcRenderer.sendSync('exportBindCode')
      const response = result.result
      if (result.isSuccess) {
        this.bindCode = response
        // 显示绑定码
        this.changeCode(4)
      }
    },
    getSn () {
      setTimeout(() => {
        const connectResult = ipcRenderer.sendSync('connectDevice')
        if (connectResult.isSuccess) {
          const result = ipcRenderer.sendSync('getSn')
          const response = result.result
          if (result.isSuccess) {
            this.SN = response
            console.log('SN:' + this.SN)
            this.firmwareVersion()
          } else {
            // 获取SN失败
            this.changeCode(5)
          }
        } else {
          // 获取SN失败
          this.changeCode(5)
        }
      }, 200)
    },
    firmwareVersion () {
      const result = ipcRenderer.sendSync('getFirmwareVersion')
      const response = result.result
      if (result.isSuccess) {
        this.cosOldVersionData = response
        this.$store.state.cosOldVersionData = response
        // this.toCosCheckUpdate()

        // TODO this.toCosCheckUpdate()
        // 升级按钮变黑
        // this.cosNewVersionData = '1.6.0'
        // this.isCosUpdate = true
        // this.$store.state.isCosUpdate = true
        // this.$store.state.cosNewVersionData = this.cosNewVersionData

        // 检查完成
        this.status = 1
      } else {
        // 获取固件版本失败
        this.changeCode(5)
      }
    },
    toCosCheckUpdate () {
      const result = ipcRenderer.sendSync('cosCheckUpdate')
      const response = result.result
      if (result.isSuccess) {
        this.cosNewVersionData = response.latestCosVersion
        this.isLatest = response.isLatest
        this.updateType = response.updateType
        this.description = response.description
        // 对比COS版本，提示用户是否升级
        this.compareByFirmwareVersion()
      } else {
        // 检查COS更新失败
        this.changeCode(5)
      }
    },
    updateFirmware () {
      this.changeCode(1)
      setTimeout(() => {
        const connectResult = ipcRenderer.sendSync('connectDevice')
        if (connectResult.isSuccess) {
          const result = ipcRenderer.sendSync('cosUpdate')
          const response = result.result
          if (result.isSuccess) {
            if (response === constants.RESULT_STATUS_SUCCESS) {
              this.isCosUpdate = false
              this.cosOldVersionData = this.cosNewVersionData
              this.$store.state.isCosUpdate = false
              this.$store.state.cosOldVersionData = this.cosNewVersionData
              this.$store.state.cosNewVersionData = this.cosNewVersionData
              this.changeCode(2)
              // 更新完cos之后需要清除缓存重新加载数据刷新页面
              // setTimeout(() => {
              //   this.init()
              //   this.changeCode(2)
              // }, 200)
            } else {
              this.isCosUpdate = true
              this.changeCode(3)
            }
          } else {
            this.isCosUpdate = true
            this.changeCode(3)
          }
        } this.isCosUpdate = true
        this.changeCode(3)
      }, 200)
    },

    downloadAndUpdate () {
      this.status = 5
      // 开始下载
      ipcRenderer.send('downloadUpdate')
      ipcRenderer.on('downloadProgress', (event, progressObj) => {
        this.progress = progressObj.percent.toFixed(0) || 0
        if (progressObj.percent === 100) {
        // 下载完成， 立刻退出并更新
          ipcRenderer.send('isUpdateNow')
        }
      })
    },
    updateNowTip () {
      this.status = 4
      this.progress = 0
    },
    updateNow () {
      // 更新显示进度
      // this.status = 5
      // setTimeout(() => {
      //   this.progress = 20
      // }, 1000)
      // setTimeout(() => {
      //   this.progress = 40
      // }, 2000)
      // setTimeout(() => {
      //   this.progress = 60
      // }, 3000)
      // setTimeout(() => {
      //   this.progress = 80
      // }, 4000)
      // setTimeout(() => {
      //   this.progress = 100
      //   this.status = 6
      // }, 5000)
      // 下载并更新
      this.downloadAndUpdate()
    },
    checkSoftUpdate () {
      // 开始检查
      ipcRenderer.send('checkForUpdate')
      // 添加自动更新事件的监听
      ipcRenderer.on('updateMessage', (event, obj) => {
        // 显示更新
        if (obj.action === 'updateAva') {
          // 显示更新
          this.status = 6
          this.softNewVersionData = obj.updateInfo.version
          this.softUpdateInfo = obj.updateInfo.releaseNotes
        } else if (obj.action === 'error') {
          this.errorInfo = obj.errorInfo
        } else if (obj.action === 'updateNotAva') {
          this.status = 1
        } else {
        }
      })
    },
    openUrl (urlType) {
      let url
      if (urlType === 'support') {
        url = 'https://support.imkey.im/'
      }
      if (urlType === 'buy') {
        url = 'https://imkey.im/'
      }
      if (urlType === 'getStarted') {
        url = 'https://imkey.im/get-started'
      }
      ipcRenderer.send('openUrl', url)
    }
  }
}
</script>

